handleOnboardingResponse(index, response) {
  const baseSpec = loadBaseSpecification() || createDefaultBaseSpec();

  switch(index) {
    case 0: // Dietary goals
      baseSpec.dietaryGoals = response;
      break;
    case 1: // Food restrictions
      const owner = loadEaters().find(e => e.isDefault) || createDefaultEater();
      owner.preferences = response;
      saveEaters([owner]);
      break;
    // Handle other questions...
  }

  saveBaseSpecification(baseSpec);
  this.askOnboardingQuestion(index + 1);
}

completeOnboarding() {
  const baseSpec = loadBaseSpecification();
  baseSpec.onboardingComplete = true;
  saveBaseSpecification(baseSpec);

  this.addMessage({
    role: 'assistant',
    content: 'Perfect! You\'re all set. Click Generate Week whenever you\'re ready to create your first meal plan.',
    timestamp: new Date().toISOString()
  });
}
```

