      const recipeId = recipeMap[recipe.name];

      meals.push({
        mealId: `meal_${crypto.randomUUID()}`,
        recipeId,
        mealType,
        date: day.date,
        eaterIds, // All eaters for now
        servings: eaters.length,
        notes: ''
      });
    }
  }

  // ... continue with transformation
}
