```javascript
// src/migrations/index.js
import { MigrationManager } from '../utils/migrationManager';

// Create migration manager
const migrationManager = new MigrationManager();

// Register migrations
migrationManager.registerMigration(1, migrateToSlice3);

// Migration functions
async function migrateToSlice3() {
  try {
    // 1. Rename storage keys
    renameStorageKey('recipes', 'vanessa_recipes');
    renameStorageKey('meals', 'vanessa_meals');
    renameStorageKey('currentMealPlan', 'vanessa_current_meal_plan');
    renameStorageKey('debug_raw_ai_output', 'vanessa_debug_raw_output');

    // 2. Create default eater if none exists
    if (!localStorage.getItem('vanessa_eaters')) {
      const defaultEater = {
        eaterId: `eater_${crypto.randomUUID()}`,
        name: 'User',
        preferences: 'No specific preferences',
        allergies: [],
        dietaryRestrictions: [],
        schedule: 'Home for dinner',
        isDefault: true,
        createdAt: new Date().toISOString(),
        updatedAt: new Date().toISOString()
      };
      localStorage.setItem('vanessa_eaters', JSON.stringify([defaultEater]));
    }

    // 3. Create base specification if none exists
    if (!localStorage.getItem('vanessa_base_specification')) {
      const eaters = JSON.parse(localStorage.getItem('vanessa_eaters') || '[]');
      const ownerEater = eaters.find(e => e.isDefault) || eaters[0] || { eaterId: null };

      const baseSpec = {
        _schemaVersion: 1,
        ownerEaterId: ownerEater.eaterId,
        weeklyBudget: 150,
        shoppingDay: 6, // Saturday
        preferredStore: '',
        householdEaterIds: eaters.map(e => e.eaterId),
        dietaryGoals: '',
        onboardingComplete: false,
        conversation: {
          startedAt: new Date().toISOString(),
          messages: []
        },
        createdAt: new Date().toISOString(),
        updatedAt: new Date().toISOString()
      };
      localStorage.setItem('vanessa_base_specification', JSON.stringify(baseSpec));
    }

    // 4. Update recipe schema
    const recipesJson = localStorage.getItem('vanessa_recipes');
    if (recipesJson) {
      const recipes = JSON.parse(recipesJson);
      const updatedRecipes = recipes.map(recipe => ({
        ...recipe,
        isFavorite: recipe.isFavorite ?? false,
        rating: recipe.rating ?? null,
        timesCooked: recipe.timesCooked ?? 0,
        lastCooked: recipe.lastCooked ?? null
      }));
      localStorage.setItem('vanessa_recipes', JSON.stringify(updatedRecipes));
    }

    return { success: true };
  } catch (error) {
    return { success: false, error: error.message };
  }
}

function renameStorageKey(oldKey, newKey) {
  const value = localStorage.getItem(oldKey);
  if (value !== null) {
    localStorage.setItem(newKey, value);
    localStorage.removeItem(oldKey);
  }
}

export default migrationManager;
```

