```javascript
// In app.js or index.js
import migrationManager from './migrations';

document.addEventListener('DOMContentLoaded', async () => {
  // Run migrations before initializing the app
  const migrationResult = await migrationManager.runMigrations();
  if (!migrationResult.success) {
    console.error('Migrations failed:', migrationResult.error);
    // Show error message to user
    showMigrationError(migrationResult);
    return;
  }

  // Initialize app
  initRouter();
});

function showMigrationError(result) {
  const app = document.getElementById('app');
  app.innerHTML = `
    <div class="error-container p-4 bg-red-100 border border-red-400 text-red-700 rounded">
      <h2 class="text-xl font-bold mb-2">Data Migration Error</h2>
      <p>There was an error updating the app data. Please try refreshing the page.</p>
      <p class="text-sm mt-2">Error: ${result.error}</p>
      ${result.details ? `<p class="text-sm">${result.details}</p>` : ''}
      <button id="retry-btn" class="mt-4 px-4 py-2 bg-blue-500 text-white rounded">
        Retry
      </button>
    </div>
  `;

  document.getElementById('retry-btn').addEventListener('click', () => {
    window.location.reload();
  });
}
